package corejavaone;

public class Switch {

	public static void main(String[] args) {
		int num = 9;
		switch(num) {
		case 1:
			System.out.println("Number is 1");
			break;
			
		case 2:
			System.out.println("Number is 9");
			break;
		case 3:
			System.out.println("Number is 4");
			break;
		default:
			System.out.println("Number not there");
			
	
		}
		
		// TODO Auto-generated method stub

	}

}
